mapper.select:
       	SELECT pi.*, ui.user_nick
		FROM post_info pi
		JOIN user_info ui ON pi.user_email = ui.user_email
		WHERE pi.gender = #{gender}
		  AND pi.style_tag = #{style_tag}
		  AND pi.season = #{season};

mapper.update:
UPDATE post_info 
SET post_img = #{postImg} 
WHERE post_idx = #{postIdx}
